import "./Signin.css";
import React from "react";
import { NavLink, Link } from "react-router-dom";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";

export const Signin = () => {
  const SignupSchema = Yup.object().shape({
    firstName: Yup.string()
      .min(2, "Too Short!")
      .max(50, "Too Long!")
      .required("Required"),
    lastName: Yup.string()
      .min(2, "Too Short!")
      .max(50, "Too Long!")
      .required("Required"),
    email: Yup.string().email("Invalid email").required("Required"),
  });

  return (
    <div className="signin-container">
      <div className="container">
        <div className="container1">
          <div className="logodiv">
            <img
              src={require("../images/logo.png")}
              alt="Logo"
              className="logo"
            />
            <img
              src={require("../images/logo1.png")}
              alt="Logo"
              className="logo1"
            />
          </div>
          <p className="text1">
            Protect & Manage every password in your business
          </p>
        </div>
        <div className="tabBar">
          <NavLink to="/signIn" className="tabBar1">
            <p className="tabBartext1">SIGN IN</p>
          </NavLink>
          <NavLink to="/signUp" className="tabBar2">
            <p className="tabBartext2"> SIGN UP </p>
          </NavLink>
        </div>
        <div className="container2">
          <p className="text2">SIGN IN TO YOUR ACCOUNT</p>
          <div className="inputcontainer">
            <Formik
              initialValues={{
                firstName: "",
                lastName: "",
                email: "",
              }}
              validationSchema={SignupSchema}
              onSubmit={(values) => {
                // same shape as initial values
                console.log(values);
              }}
            >
              {({ errors, touched }) => (
                <Form>
                  <Field
                    type="text"
                    placeholder="   Mobile Number"
                    onChange={(e) => {
                      console.log(e.target.value);
                    }}
                    className="inputField1"
                  />
                  <Field
                    type="password"
                    placeholder="   Password"
                    onChange={(e) => {
                      console.log(e.target.value);
                    }}
                    className="inputField2"
                  />
                  <p className="text3">Forgot your password?</p>
                  <div style={{ borderWidth: 5 }}>
                    <button className="button">
                      <p className="buttontext">SIGN IN</p>
                    </button>
                  </div>
                </Form>
              )}
            </Formik>
          </div>
          <p>
            <Link to="/signUp" className="text4">
              Donâ€™t have a Account? SIGNUP
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};
